    private void Rb3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Rb3ActionPerformed
        // TODO add your handling code here:
        if(Rb3.isSelected())
        {
            Rb7.disable();
            Rb5.enable();
            Rb6.enable();
            Rb8.enable();
            float a=Float.parseFloat(T1.getText());
            if(Rb5.isSelected())
            {
                float b=(float)a*64;
                L5.setText(Rb3.getText());
                L6.setText(T1.getText());
                L7.setText(Rb5.getText());
                L8.setText(String.valueOf(b));
            }
            else if(Rb6.isSelected())
            {
                float b=(float)a*64;
                float c=(float)b/18;
                L5.setText(Rb3.getText());
                L6.setText(T1.getText());
                L7.setText(Rb5.getText());
                L8.setText(String.valueOf(c));
            }
            else if(Rb8.isSelected())
            {
                float b=(float)a*64;
                float c=(float)b/85;
                L5.setText(Rb3.getText());
                L6.setText(T1.getText());
                L7.setText(Rb8.getText());
                L8.setText(String.valueOf(c));
            }
            else{}
        }
        else{}
    }//GEN-LAST:event_Rb3ActionPerformed

